
public class Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Teste da classe, Tema Casting");
		int x;
		int y;
		double resultado;
		
		x = 10;
		y = 3;
		resultado = (double) x / y;
		System.out.printf("Impressao da subtração %.2f", resultado);
	}

}
